import { Button, Form, Input, message } from "antd";
import React, { useRef } from "react";
import styled from "styled-components";
import Link from "next/link";
import { Fade, Flip } from "react-reveal";
import axios from "axios";
import Router from "next/router";

const Wrapper = styled.div`
  width: 100%;
  margin-top: 50px;
`;

const PageTitle = styled.h3`
  font-size: 22px;
  font-weight: 700;
  margin-bottom: 40px;
`;

const WriteTextArea = styled(Input.TextArea)`
  resize: none;
`;

const BWrapper = styled.div`
  width: 100%;
  margin-bottom: 5px;

  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: flex-end;
`;

const Write = () => {
  const writeFormRef = useRef();

  const finishAction = async (value) => {
    console.log(value);
    const sendData = {
      _title: value.title,
      _content: value.content,
      _author: value.author,
    };
    const response = await axios.post(
      "http://localhost:4000/api/board/write",
      sendData
    );
    console.log(response);
    if (response.data.result === true) {
      Router.push("/");
      message.success("게시글 등록이 완료되었습니다.");
    } else {
      message.error("게시글 등록이 실패했습니다.");
    }
  };
  return (
    <Wrapper>
      <Flip top>
        <PageTitle>Write</PageTitle>
      </Flip>

      <Fade bottom>
        <Form
          ref={writeFormRef}
          labelCol={{ span: 3 }}
          wrapperCol={{ span: 21 }}
          onFinish={finishAction}
        >
          {/**제목 */}
          <Form.Item label="제목" name="title" rules={[{ required: true }]}>
            <Input />
          </Form.Item>
          {/**작성자 */}
          <Form.Item label="작성자" name="author" rules={[{ required: true }]}>
            <Input />
          </Form.Item>
          {/**본문 */}
          <Form.Item label="본문" name="content" rules={[{ required: true }]}>
            <WriteTextArea rows={10} />
          </Form.Item>

          <BWrapper>
            <Link href="/">
              <a>
                <Button size="small" type="default">
                  목록으로
                </Button>
              </a>
            </Link>

            <Button size="small" type="primary" htmlType="submit">
              등록
            </Button>
          </BWrapper>
        </Form>
      </Fade>
    </Wrapper>
  );
};

export default Write;
