import { Button, Table } from "antd";
import React from "react";
import styled from "styled-components";
import Link from "next/link";
import { Fade, Flip } from "react-reveal";

const Wrapper = styled.div`
  width: 100%;
  margin-top: 50px;
`;

const PageTitle = styled.h3`
  font-size: 22px;
  font-weight: 700;
  margin-bottom: 40px;
`;

const BWrapper = styled.div`
  width: 100%;
  margin-bottom: 5px;

  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: flex-end;
`;

const MyApp = () => {
  //화면에 보일 명칭과 데이터베이스 컬럼 이름을 매칭시킨다.
  const columns = [
    {
      title: "번호",
      dataIndex: "id",
    },
    {
      title: "제목",
      render: (data) => (
        <Link href="/detail">
          <div>{data.title}</div>
        </Link>
      ),
    },
    {
      title: "작성자",
      dataIndex: "author",
    },
    {
      title: "작성일",
      dataIndex: "createdAt",
    },
    {
      title: "조회수",
      dataIndex: "hit",
    },
  ];

  const datum = [
    {
      id: "5",
      title: "이게 게시판 디자인이구나?",
      author: "석만이",
      createdAt: "2022-03-04",
      hit: "43",
    },
    {
      id: "4",
      title: "얄라리얄라숑",
      author: "개똥이",
      createdAt: "2022-03-09",
      hit: "33",
    },
    {
      id: "3",
      title: "후다닥",
      author: "멍청이",
      createdAt: "2022-02-09",
      hit: "546",
    },
    {
      id: "2",
      title: "꺄르륵",
      author: "바보",
      createdAt: "2022-01-09",
      hit: "11",
    },
    {
      id: "1",
      title: "우이씨",
      author: "간지",
      createdAt: "2022-02-25",
      hit: "23",
    },
  ];
  return (
    <Wrapper top>
      <Flip>
        <PageTitle>Next Board</PageTitle>
      </Flip>

      {/**버튼 색션 */}
      <Fade bottom>
        <BWrapper>
          <Link href="/write">
            <a>
              <Button type="primary" size="small">
                글쓰기
              </Button>
            </a>
          </Link>
        </BWrapper>

        <Table size="small" columns={columns} dataSource={datum} rowKey="id" />
      </Fade>
    </Wrapper>
  );
};

export default MyApp;
